import React, { useState, useEffect } from "react";


import { useParams } from "react-router-dom";
import Helmet from "../components/Helmet/Helmet";
import CommonSection from "../components/UI/common-section/CommonSection";
import { Container, Row, Col } from "reactstrap";

import { useDispatch } from "react-redux";
import { cartActions } from "../store/shopping-cart/cartSlice";

import "../styles/product-details.css";

import ProductCard from "../components/UI/product-card/ProductCard";

const FoodDetails = () => {
  const [produits, setProduits] = useState([]);
  const { ref } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    fetch('http://localhost:8060/product/')
      .then(response => response.json())
      .then(data => {
        setProduits(data.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const product = produits.find((item) => item.ref === ref);

  if (!product) {
    return <p>Product not found.</p>; // Handle case where product is not found
  }

  const {
    name,
    category,
    price,
    description,
    gallerie,

    // ... other properties
  } = product;

  console.log(description)
  const relatedProduct = produits.filter((item) => category[0] === item.category[0]);

  const addItem = () => {
    dispatch(
      cartActions.addItem({
       ref,
       name,
        price,
        gallerie,
      })
    );
  };


