import React, { useState, useEffect } from "react";
import "../../../styles/product-card.css";

import { Link } from "react-router-dom";

import { useDispatch } from "react-redux";
import { cartActions } from "../../../store/shopping-cart/cartSlice";

const ProductCard = (props) => {
  const {
    _id,
    ref,
    name,
    price,
    description,
    gallerie,
    pourcentage,
    Marque,
    category,
    createdAt,
    dateDebutPromotion,
    dateFinPromotion,
    inStock,
    v,
  } = props.item;

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(
      cartActions.addItem({
        _id,
        name,
        gallerie,
        price,
      })
    );
  };

  const [produits, setProduits] = useState([]);
  useEffect(() => {
    fetch('http://localhost:8060/product/')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setProduits(data.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="product__item">
      <div className="product__img">
        {produits &&
          produits.map((obj) => {
            let matchingImage = null;
            for (let i = 0; i < obj.gallerie.length; i++) {
              console.m
              if (obj.gallerie[i].name === name) {
                matchingImage = obj.gallerie[i].name;

                break; // Exit the loop once the matching image is found
              }
            }

            if (matchingImage) {
              return (
                <img

                  src={matchingImage}
                  alt={`product-img`}
                  className="w-50"
                />
              );
            }
            return null;
          })}
      </div>

      <div className="product__content">
        <div className="heg">
          <Link to={`/foods/${_id}`}>{name}</Link>
        </div>
        <div className="extra d-flex align-items-center justify-content-between">
          <span className="product__price">${price}</span>
          <button className="addTOCart__btn" onClick={addToCart}>
            Ajouter au panier
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
