import React from "react";
import "../../../styles/product-card.css";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { cartActions } from "../../../store/shopping-cart/cartSlice";

const ProductCard = (props) => {
  const {
    _id,
    ref,
    name,
    price,
    description,
    gallerie,
    pourcentage,
    Marque,
    category,
    createdAt,
    dateDebutPromotion,
    dateFinPromotion,
    inStock,
    v,
  } = props.item;

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(
      cartActions.addItem({
        _id,
        name,
        gallerie, // Use the destructured 'gallerie' array from props
        price,
      })
    );
  };

  return (
    <div className="product__item">
      <div className="product__img">
        {/* Map over the 'gallerie' array and display images */}
        {gallerie && gallerie.map((item, index) => (
          <img key={index} src={item.name} alt={`product-img-${index}`} className="w-50" />
        ))}
      </div>

      <div className="product__content">
        <div className="heg">
          <Link >{name}</Link>
        </div>
        <div className="extra d-flex align-items-center justify-content-between">
          <span className="product__price">${price}</span>
          <button className="addTOCart__btn" onClick={addToCart}>
            Ajouter au panier
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
