import React, { useState,useEffect } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "reactstrap";
import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import "../styles/checkout.css";

const Checkout = () => {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    fetch('http://localhost:8060/api/users/getall')
      .then(response => response.json())
      .then(data => {
        setUsers(data.data);

      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  console.log(users)


  const [enterName, setEnterName] = useState("");

  const [enterNumber, setEnterNumber] = useState("");

  const [enterCity, setEnterCity] = useState("");
  const [payement, setPayement] = useState("");

  const shippingInfo = [];
  //Amount
  const cartTotalAmount = useSelector((state) => state.cart.totalAmount);
  const shippingCost = 7;

  const totalAmount = cartTotalAmount + Number(shippingCost);
  //Items
  const cartItems = useSelector((state) => state.cart.cartItems);
  const produits= cartItems.map((item) => (
    item.ref
  ))

  const quant= cartItems.map((item) => (
    item.quantity
  ))

  const sum = quant.reduce((accumulator, currentValue) => accumulator + currentValue, 0);
  const currentDate = new Date();
  const formattedCurrentDate = currentDate.toISOString().split('T')[0] + "T00:00:00.000Z";
  const submitHandler = (e) => {
    e.preventDefault();
    const userShippingAddress = {
      client: [enterName],
      orderDate:formattedCurrentDate ,
      phone: enterNumber,
      quantity:sum,
      deliveryAddress: enterCity,
      paymentMethod: payement,
      product:produits,
      totalPrice:totalAmount,
      panier: "",
      __v: 0
    };

    shippingInfo.push(userShippingAddress);

  };
  console.log(shippingInfo)

  fetch('http://localhost:8060/order/create', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ data: shippingInfo }),
  })
    .then(response => response.json())
    .then(data => {
      console.log('New users added successfully:', data);
      console.log('Added Order ID:', data._id);
      console.log('Client Name:', data.client);
      console.log('Order Date:', data.orderDate);
    })
    .catch(error => {
      console.error('Error adding new users:', error);
    });


  return (
    <Helmet title="Checkout">
      <CommonSection title="Checkout" />
      <section>
        <Container>
          <Row>
            <Col lg="8" md="6">
              <h6 className="mb-4">Vos infos</h6>
              <form className="checkout__form" onSubmit={submitHandler}>
                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Nom et Prenom"
                    required
                    onChange={(e) => setEnterName(e.target.value)}
                  />
                </div>


                <div className="form__group">
                  <input
                    type="number"
                    placeholder="numèro de télèphone"
                    required

                    onChange={(e) => setEnterNumber(e.target.value)}
                  />
                </div>

                <div className="form__group">
                  <input
                    type="text"
                    placeholder="Adresse"
                    required
                    onChange={(e) => setEnterCity(e.target.value)}
                  />
                </div>
                <div className="form__group">
                 <select
                  required
                  className="payment-select"
                  onChange={(e) => setPayement(e.target.value)}
                >
                  <option value="" disabled selected>Selectionner la methode de payement</option>
                  <option value="credit">Credit</option>
                  <option value="cash">Cash</option>
                </select>
                </div>
                <button type="submit" className="addTOCart__btn">
                  Commander
                </button>
              </form>
            </Col>

            <Col lg="4" md="6">
              <div className="checkout__bill">
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                  Sur-total: <span>${cartTotalAmount}</span>
                </h6>
                <h6 className="d-flex align-items-center justify-content-between mb-3">
                 Livraison: <span>${shippingCost}</span>
                </h6>
                <div className="checkout__total">
                  <h5 className="d-flex align-items-center justify-content-between">
                    Total: <span>${totalAmount}</span>
                  </h5>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default Checkout;
