import React from "react";
import "../../../styles/product-card.css";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { cartActions } from "../../../store/shopping-cart/cartSlice";

const ProductCard = (props) => {
  const {
    _id,
    ref,
    name,
    price,
    description,
    gallerie,
    pourcentage,
    Marque,
    category,
    createdAt,
    dateDebutPromotion,
    dateFinPromotion,
    inStock,
    v,
  } = props.item;

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(
      cartActions.addItem({
        _id,
        name,
        gallerie,
        price,
      })
    );
  };

  return (
    <div className="product__item">
      <div className="product__img">
        {gallerie &&
          gallerie.map((ite, index) => {
            // Find the corresponding image in the gallerie array
            const correspondingImage = gallerie.find(
              (galleryItem) => galleryItem.name = ite.name
            );

            return (
              <React.Fragment key={index}>
                {correspondingImage && (
                  <img
                    src={correspondingImage.name}
                    alt={`product-img`}
                    className="w-50"
                  />
                )}
              </React.Fragment>
            );
          })}
      </div>
      <div className="product__content">
        <div className="heg">
          <Link to={`/foods/${_id}`}>{name}</Link>
        </div>
        <div className="extra d-flex align-items-center justify-content-between">
          <span className="product__price">${price}</span>
          <button className="addTOCart__btn" onClick={addToCart}>
            Ajouter au panier
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
