import React, { useState, useEffect } from "react";
import "../../../styles/product-card.css";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { cartActions } from "../../../store/shopping-cart/cartSlice";

const ProductCard = (props) => {
  const {
    _id,
    ref,
    name,
    price,
    description,
    gallerie,
    pourcentage,
    Marque,
    category,
    createdAt,
    dateDebutPromotion,
    dateFinPromotion,
    inStock,
    v,
  } = props.item;

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(
      cartActions.addItem({
        _id,
        name,
        gallerie,
        price,
      })
    );
  };

  const [produits, setProduits] = useState([]);
  useEffect(() => {
    fetch('http://localhost:8060/product/')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setProduits(data.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const x = ["a"]; // Not sure how you're using this array, consider revising if needed

  return (
    <div className="product__item">
      <div className="product__img">
        {x.map((ind) =>
          produits && produits.map((obj) => (
            <React.Fragment key={ob}>
              {obj.gallerie.map((i) => (
                <React.Fragment key={i.name}>
                  {gallerie && gallerie.map((ite, index) => (
                    ite.name === obj.name ? (
                      <img key={index} src={ite.name} alt={`product-img-${index}`} className="w-50" />
                    ) : null
                  ))}
                </React.Fragment>
              ))}
            </React.Fragment>
          ))
        )}
      </div>

      <div className="product__content">
        <div className="heg">
          <Link to={`/foods/${_id}`}>{name}</Link>
        </div>
        <div className="extra d-flex align-items-center justify-content-between">
          <span className="product__price">${price}</span>
          <button className="addTOCart__btn" onClick={addToCart}>
            Ajouter au panier
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
