import React, { useState } from 'react';
import './Jewelry.css';

const JewelryEcommerce = () => {
    const [toggleTab, setToggleTab] = useState(1);

    const toggleState = (index) => {
        setToggleTab(index);
    };

    return (
        <>
            <section className="about-us">
                <div className="row">
                    <div className="column">
                        <div className="tabs">
                            <div
                                className={toggleTab === 1 ? 'single-tab active-tab' : 'single-tab'}
                                onClick={() => toggleState(1)}
                            >
                                <h2>Our Addresses</h2>
                            </div>
                            <div
                                className={toggleTab === 2 ? 'single-tab active-tab' : 'single-tab'}
                                onClick={() => toggleState(2)}
                            >
                                <h2>Contact Information</h2>
                            </div>
                            <div
                                className={toggleTab === 3 ? 'single-tab active-tab' : 'single-tab'}
                                onClick={() => toggleState(3)}
                            >
                                <h2>About Us</h2>
                            </div>
                        </div>
                        <div className="tab-content">
                            {toggleTab === 1 && (
                                <div className="content active-content">
                                    <h2>Our Addresses</h2>
                                    <p>We have locations in several cities:</p>
                                    <ul>
                                        <li>Address 1: City, Country</li>
                                        <li>Address 2: City, Country</li>
                                        {/* Add more addresses as needed */}
                                    </ul>
                                </div>
                            )}

                            {toggleTab === 2 && (
                                <div className="content active-content">
                                    <h2>Contact Information</h2>
                                    <p>You can reach us through various channels:</p>
                                    <ul>
                                        <li>Phone: +1234567890</li>
                                        <li>Email: info@example.com</li>
                                        <li>Follow us on social media:</li>
                                        <ul>
                                            <li>Facebook: @ExampleCompany</li>
                                            <li>Twitter: @ExampleCompany</li>
                                            {/* Add more social media accounts as needed */}
                                        </ul>
                                    </ul>
                                </div>
                            )}

                            {toggleTab === 3 && (
                                <div className="content active-content">
                                    <h2>About Us</h2>
                                    <p>We are a leading e-commerce company specializing in high-quality jewelry. Our mission is to provide our customers with exquisite and elegant jewelry pieces. We take pride in our craftsmanship and commitment to customer satisfaction.</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
};

export default JewelryEcommerce;
