import React, { useState } from 'react';
import './Jewelry.css';

const JewelryEcommerce = () => {
    const [toggleTab, setToggleTab] = useState(1);

    const toggleState = (index) => {
        setToggleTab(index);
    };

    return (
        <>
            <section className="jewelry">
                <div className="row">
                    <div className="column">
                        <div className="tabs">
                            <div
                                className={toggleTab === 1 ? 'single-tab active-tab' : 'single-tab'}
                                onClick={() => toggleState(1)}
                            >
                                <h2>Necklaces</h2>
                            </div>
                            <div
                                className={toggleTab === 2 ? 'single-tab active-tab' : 'single-tab'}
                                onClick={() => toggleState(2)}
                            >
                                <h2>Rings</h2>
                            </div>
                            <div
                                className={toggleTab === 3 ? 'single-tab active-tab' : 'single-tab'}
                                onClick={() => toggleState(3)}
                            >
                                <h2>Earrings</h2>
                            </div>
                        </div>
                        <div className="tab-content">
                            {toggleTab === 1 && (
                                <div className="content active-content">
                                    <h2>Stunning Necklaces Collection</h2>
                                    <p>Explore our exquisite collection of necklaces.</p>
                                    <div className="jewelry-items">
                                        {/* Display necklace items */}
                                    </div>
                                </div>
                            )}

                            {toggleTab === 2 && (
                                <div className="content active-content">
                                    <h2>Elegant Rings Collection</h2>
                                    <p>Discover our beautifully crafted rings.</p>
                                    <div className="jewelry-items">
                                        {/* Display ring items */}
                                    </div>
                                </div>
                            )}

                            {toggleTab === 3 && (
                                <div className="content active-content">
                                    <h2>Chic Earrings Collection</h2>
                                    <p>Shop our stylish collection of earrings.</p>
                                    <div className="jewelry-items">
                                        {/* Display earring items */}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
};

export default JewelryEcommerce;
