import React, { useState, useEffect } from "react";


import { useParams } from "react-router-dom";
import Helmet from "../components/Helmet/Helmet";
import CommonSection from "../components/UI/common-section/CommonSection";
import { Container, Row, Col } from "reactstrap";

import { useDispatch } from "react-redux";
import { cartActions } from "../store/shopping-cart/cartSlice";

import "../styles/product-details.css";

import ProductCard from "../components/UI/product-card/ProductCard";

const FoodDetails = () => {
  const [produits, setProduits]=useState([])
  useEffect(() => {
    fetch('http://localhost:8060/product/')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setProduits(data.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);






  const [tab, setTab] = useState("desc");
  const [enteredName, setEnteredName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [reviewMsg, setReviewMsg] = useState("");
  const { ref } = useParams();
  const dispatch = useDispatch();

  const product = produits.find((product) => product.ref === ref);



  const { name, price, category, description, gallerie } = product;

  const relatedProduct = produits.filter((item) => category === item.category);

  const addItem = () => {
    dispatch(
      cartActions.addItem({
       ref,
       name,
        price,
        gallerie,
      })
    );
  };

  const submitHandler = (e) => {
    e.preventDefault();

    console.log(enteredName, enteredEmail, reviewMsg);
  };


  useEffect(() => {
    window.scrollTo(0, 0);
  }, [product]);

  return (
    <Helmet title="Product-details">
      <CommonSection title={name} />

      <section>
        <Container>
          <Row>
            <Col lg="2" md="2">
              <div className="product__images ">
                <div
                  className="img__item mb-3"

                >
                  <img src={product.image01} alt="" className="w-50" />
                </div>
                <div
                  className="img__item mb-3"

                >
                  <img src={product.image02} alt="" className="w-50" />
                </div>

                <div
                  className="img__item"

                >
                  <img src={product.image03} alt="" className="w-50" />
                </div>
              </div>
            </Col>

            <Col lg="4" md="4">
              <div className="product__main-img">
              {gallerie &&
          gallerie.map((image, index) => (
            <React.Fragment key={index}>
            <img
          src={`http://localhost:8060/getImage/${image.name}`} // Updated image source
          alt="product-img"
        />
            </React.Fragment>))}


              </div>
            </Col>

            <Col lg="6" md="6">
              <div className="single__product-content">
                <h2 className="product__title mb-3">{name}</h2>
                <p className="product__price">
                  {" "}
                  Prix: <span>${price}</span>
                </p>
                <p className="category mb-5">
                  Categorie: <span>{category}</span>
                </p>

                <button onClick={addItem} className="addTOCart__btn">
                  Ajouter au panier
                </button>
              </div>
            </Col>

            <Col lg="12">
              <div className="tabs d-flex align-items-center gap-5 py-3">
                <h6
                  className={` ${tab === "desc" ? "tab__active" : ""}`}
                  onClick={() => setTab("desc")}
                >
                  Description
                </h6>

              </div>


                <div className="tab__content">
                  <p>{description}</p>
                </div>

            </Col>

            <Col lg="12" className="mb-5 mt-4">
              <h2 className="related__Product-title">Vous pourriez Ã©galement aimer</h2>
            </Col>

            {relatedProduct.map((item) => (
              <Col lg="3" md="4" sm="6" xs="6" className="mb-4" key={item.id}>
                <ProductCard item={item} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default FoodDetails;
